import React, { useContext } from 'react';
import { Alert as AlertUI } from 'evergreen-ui';
import AlertContext from 'context/Alert/AlertContext';

function Alert() {

    const { hide, state } = useContext(AlertContext);

    if (!state) {
        return null;
    }

    const { type, text } = state;

    return (
        <AlertUI
            appearance="card"
            intent={type}
            title={text}
            marginBottom={32}
            isRemoveable
            onRemove={hide}
        />
    );
}

export default Alert;