import React from 'react';
import { Icon } from 'evergreen-ui';

import './repositories.scss';

function Repository({ html_url, name, description, stargazers_count, language }) {

    return (
        <div className="repository">
            <h5 className="repository-title">
                <Icon icon="git-repo" size={16} color="#586069" />
                <a
                    href={html_url}
                    target="_blank"
                    rel="noopener noreferrer"
                >
                    {name}
                </a>
            </h5>
            <p className="repository-description">{description}</p>

            <div className="repository-meta">
                {language && (
                    <span>
                        <Icon
                            icon="code"
                            size={16}
                            color="#586069"
                        />
                        {language}
                    </span>
                )}

                <span className="card-star">
                    <Icon
                        icon="star-empty"
                        size={16}
                        color="#586069"
                    />
                    {stargazers_count}
                </span>
            </div>
        </div>
    );
}

export default Repository;