import React from 'react';
import { Button } from 'evergreen-ui';

import './repositories.scss';

function Repository({ html_url, name, description, stargazers_count, language, updated_at }) {

    return (
        <div className="repository">
            <div>
                <h3 children="repository-title">
                    <a
                        href={html_url}
                        target="_blank"
                        rel="noopener noreferrer"
                    >
                        {name}
                    </a>
                </h3>
            </div>
            <div>
                <p>{description}</p>
            </div>
            <div></div>
            <div>
                {language}
                <Button iconBefore="star-empty">{stargazers_count}</Button>
                Updated on {new Date(updated_at).toLocaleString()}
            </div>
        </div>
    );
}

export default Repository;