import React from 'react';
import {Button, Icon} from "evergreen-ui";

import './user.scss';

function User({ login, name, html_url,  avatar_url, bio, blog, company, email, location, created_at }) {

    return (
        <div className="user">
            <div className="user_img">
                <img src={avatar_url} alt={name} />
            </div>

            <div className="user_body">
                <div className="user_title">
                    <h1>
                        <a
                            href={html_url}
                            target="_blank"
                            rel="noopener noreferrer"
                        >
                            {name}
                        </a>
                    </h1>
                    <span className="user_subtitle">{login}</span>
                </div>

                <Button
                    is="a"
                    href={html_url}
                    target="_blank"
                    rel="noopener noreferrer"
                    marginBottom={16}
                    width={272}
                    justifyContent="center"
                >Open</Button>

                <div className="user_bio">{bio}</div>

                <ul className="user_details">
                    {company && (
                        <li>
                            <Icon icon="compass" size={16} color="#586069" />
                            {company}
                        </li>
                    )}

                    {location && (
                        <li>
                            <Icon icon="globe" size={16} color="#586069" />
                            {location}
                        </li>
                    )}

                    {email && (
                        <li>
                            <Icon icon="envelope" size={16} color="#586069" />
                            {email}
                        </li>
                    )}

                    {blog && (
                        <li>
                            <Icon icon="link" size={16} color="#586069" />
                            {blog}
                        </li>
                    )}
                </ul>
            </div>
        </div>
    );
}

export default User;